-- ----------------------------
-- Table structure for content
-- ----------------------------
DROP TABLE IF EXISTS `content`;
CREATE TABLE `content` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` char(100) NOT NULL DEFAULT '' COMMENT '标题',
  `content` text COMMENT '正文',
  `addtime` int(10) NOT NULL DEFAULT '0' COMMENT '添加时间',
  `click` mediumint(8) unsigned NOT NULL DEFAULT '100' COMMENT '查看次数',
  `flag` set('置顶','推荐') DEFAULT NULL COMMENT '文章属性',
  `category_cid` smallint(6) NOT NULL COMMENT '栏目id',
  `admin_aid` smallint(5) unsigned NOT NULL COMMENT '管理员id',
  `keywords` varchar(255) NOT NULL,
  `description` varchar(255) NOT NULL,
  `thumb` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `fk_content_category_idx` (`category_cid`),
  KEY `fk_content_admin1_idx` (`admin_aid`)
) ENGINE=MyISAM AUTO_INCREMENT=41 DEFAULT CHARSET=utf8 COMMENT='文章表';

